package week1.day1;

public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	
		int c = 20;
		System.out.print("Numbers which are odd from 1 to "+c+" are shown here:");
		for (int i = 1; i <= c; i++)
		{
		   if (i % 2 == 1) {
			System.out.print(i+" ");
   }
  }
 }
}
